UPDATE quest_template SET RequestItemsText='', OfferRewardText='If nothin'' else, you bought us some time, and some room to open up our supply lines through this siege./n Yer a fine $C.', PrevQuestID=27986, NextQuestID=0, NextQuestInChain=0 WHERE entry=28028; -- Siege!
